// *******************************************************************************************// DialogsAndAlerts.h                                                      CLASSIC EVENT MODEL// *******************************************************************************************// // This program initially opens a small modal dialog which is automatically closed after 10// seconds, the timeout value having been set by a call to SetDialogTimeout.  The program// then://// ╔	Opens a window for the purposes of displaying	advisory text and proving correct window//		updating and activation/deactivation in the	presence of alerts and dialogs.//// ╔	Allows the user to invoke, via the Demonstration menu, modal and movable modal alerts//		and dialogs, a modeless dialog and, on Mac OS X, a window-modal alert and dialog//		(i.e., sheets).  //// The modal alert box is created programmatically using the StandardAlert function.//// The movable modal alert is created programmatically using the StandardAlert function on Mac// OS 9 and the CreateStandardAlert function on Mac OS X.//// The modal dialog contains three checkboxes in one group box, and two pop-up menu buttons in// another group box.//// The movable modal dialog contains four radio buttons in one group box, and a clock control// and edit text item in another group box.//// The modeless dialog contains, amongst other items, an edit text item.//// The modal and movable modal alerts and dialogs use an application-defined event filter// (callback) function.//// The program utilises the following resources://// ╔	A 'plst' resource.//// ╔	An 'MBAR' resource, and 'MENU' resources for Apple, File, and Demonstration pull-down//		menus, and the pop-up menu buttons (preload, non-purgeable).//// ╔	A 'WIND' resource (purgeable) (initially visible).//// ╔	'DLOG' resources (purgeable) (initially not visible) and associated 'DITL' resources//		(purgeable), 'dlgx' resources (purgeable), and 'dftb' resources (non-purgeable, but//		'dftb' resources are automatically marked purgeable when read in).//// ╔	'CNTL' resources for primary group boxes, separator lines, pop-up menu buttons, a clock,//		and an image well (all purgeable).//// ╔	'STR#' resources (purgeable) containing the message and informative text for the alerts.//// ╔	A 'cicn' resource (purgeable) for the modeless dialog box.//// ╔	A 'ppat' resource (purgeable), which is used to colour the content region of the//		document window for update proving purposes.//// ╔	'hdlg' resources (purgeable) containing balloon help information for the modal and//		movable modal dialog.//// ╔	An 'hrct' resource and associated 'hwin' resource (both purgeable) containing balloon//		help information for the modeless dialog.//// ╔	A 'SIZE' resource with the acceptSuspendResumeEvents, canBackground, //		doesActivateOnFGSwitch, and isHighLevelEventAware flags set.//// *******************************************************************************************// ииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии includes#include <Carbon.h>// иииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии defines#define rMenubar									128#define mAppleApplication					128#define  iAbout										1#define mFile											129#define  iClose										4#define  iQuit										12#define mEdit											130#define  iCut											3#define  iCopy										4#define  iPaste										5#define  iClear										6#define mDemonstration						131#define  iModalAlert							1#define  iMovableAlert						2#define  iModalDialog							3#define  iMovableModalDialog			4#define  iModeless								5#define  iWindowModalAlert				7#define  iWindowModalDialog				8#define mFont											132#define rWindow										128#define rSplash										128#define rModalDialog							129#define  iGridSnap								4#define  iShowGrid								5#define  iShowRulers							6#define  iFont										11#define  iSound										12#define rMovableModalDialog				130#define  iCharcoal								7#define  iOilPaint								8#define  iPencil									9#define  iChalk										10#define  iClockOne								12#define rModelessDialog						131#define  iEditTextSearchModeless	2#define rSheetDialog							132#define  iEditTextSheetDialog			2#define rAlertStrings							128#define  sModalMessage						1#define  sModalInformative				2#define  sMovableMessage					3#define  sMovableInformative			4#define rSheetStrings							132#define  sAlertSheetMessage				1#define  sAlertSheetInformative		2#define rPixelPattern							128#define kSearchModeless						1#define kSheetDialog							2#define kReturn										(SInt8) 0x0D#define kEnter										(SInt8) 0x03#define kEscape										(SInt8) 0x1B#define kPeriod										(SInt8) 0x2E#define MAX_UINT32								0xFFFFFFFF// иииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии function prototypesvoid		main														(void);void		doPreliminaries									(void);OSErr		quitAppEventHandler							(AppleEvent *,AppleEvent *,SInt32);void		eventLoop												(void);void		doIdle													(void);void		doEvents												(EventRecord *);void		doMouseDown											(EventRecord *);void		doKeyDown												(EventRecord *);void		doUpdate												(EventRecord *);void		doUpdateDocument								(WindowRef);void		doActivate											(EventRecord *);void		doActivateDocument							(WindowRef,Boolean);void		doActivateDialogs								(EventRecord *,Boolean);void		doOSEvent												(EventRecord *);void		doAdjustMenus										(void);void		doMenuChoice										(SInt32);void		doEditMenu											(MenuItemIndex);void		doDemonstrationMenu							(MenuItemIndex);void		doExplicitlyDeactivateDocument	(void);Boolean	doModalAlerts										(Boolean);Boolean	doMovableModalAlertOnX					(void);Boolean	doModalDialog										(void);Boolean	doMovableModalDialog						