// *******************************************************************************************// BevelImagePictIcon.c// *******************************************************************************************// ииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии includes#include "Controls3.h"// ииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии global variablesWindowRef gWindowRef;extern Boolean	gRunningOnX;extern Str255		gCurrentString;extern Boolean	gInBackground;// ********************************************************************** doBevelImagePictIconvoid  doBevelImagePictIcon(void){	BevelDocStrucHandle	bevelDocStrucHdl;	// иииииииииииииииииииииииииииииииииииииииииииииииии initial advisory text for window header	doCopyPString("\pBalloon (OS 8/9) and Help tag (OS X) help is available",gCurrentString);	// иииииииииииии open a window, set font size, set theme-compliant colour/pattern for window	if(!(gWindowRef = GetNewCWindow(rBevelImageWindow,NULL,(WindowRef)-1)))		ExitToShell();	SetPortWindowPort(gWindowRef);	UseThemeFont(kThemeSmallSystemFont,smSystemScript);	SetThemeWindowBackground(gWindowRef,kThemeBrushDialogBackgroundActive,true);	// иииииииииии get block for document structure, assign handle to window record refCon field	if(!(bevelDocStrucHdl = (BevelDocStrucHandle) NewHandle(sizeof(BevelDocStruc))))		ExitToShell();	SetWRefCon(gWindowRef,(SInt32) bevelDocStrucHdl);	// ииииииииииииииииииииииииииииииииииииииии get controls, help tags if OS X, and show window	doGetControls(gWindowRef);//	if(gRunningOnX)		helpTagsBevelImagePictIcon(gWindowRef);	ShowWindow(gWindowRef);}// ***************************************************************************** doGetControlsvoid  doGetControls(WindowRef windowRef){	ControlRef									controlRef;	BevelDocStrucHandle					bevelDocStrucHdl;	ThemeButtonKind							buttonKind = kThemeRoundedBevelButton;	ControlButtonTextPlacement	textPlacementBelow = kControlBevelButtonPlaceBelowGraphic;	ControlButtonTextPlacement	textPlacementAbove = kControlBevelButtonPlaceAboveGraphic;	Boolean											centrePopupGlyph = true;	ControlFontStyleRec					controlFontStyleStruc;	// иииииииииииииии create root control for window, get handle to window's document structure	if(!gRunningOnX)		CreateRootControl(windowRef,&controlRef);	bevelDocStrucHdl = (BevelDocStrucHandle) (GetWRefCon(windowRef));	// ииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии get the controls	if(!((*bevelDocStrucHdl)->bevelButton1Ref = GetNewControl(cBevelButton1,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton2Ref = GetNewControl(cBevelButton2,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton3Ref = GetNewControl(cBevelButton3,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton4Ref = GetNewControl(cBevelButton4,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton5Ref = GetNewControl(cBevelButton5,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton6Ref = GetNewControl(cBevelButton6,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton7Ref = GetNewControl(cBevelButton7,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton8Ref = GetNewControl(cBevelButton8,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton9Ref = GetNewControl(cBevelButton9,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton10Ref = GetNewControl(cBevelButton10,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton11Ref = GetNewControl(cBevelButton11,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton12Ref = GetNewControl(cBevelButton12,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton13Ref = GetNewControl(cBevelButton13,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton14Ref = GetNewControl(cBevelButton14,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton15Ref = GetNewControl(cBevelButton15,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton16Ref = GetNewControl(cBevelButton16,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton17Ref = GetNewControl(cBevelButton17,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton18Ref = GetNewControl(cBevelButton18,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton19Ref = GetNewControl(cBevelButton19,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton20Ref = GetNewControl(cBevelButton20,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->bevelButton21Ref = GetNewControl(cBevelButton21,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->imageWell1Ref = GetNewControl(cImageWell1,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->imageWell2Ref = GetNewControl(cImageWell2,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->picture1Ref = GetNewControl(cPicture1,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->picture2Ref = GetNewControl(cPicture2,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->colourIcon1Ref = GetNewControl(cColourIcon1,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->colourIcon2Ref = GetNewControl(cColourIcon2,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->iconSuite1Ref= GetNewControl(cIconSuite1,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->iconSuite2Ref= GetNewControl(cIconSuite2,windowRef)))		ExitToShell();	if(!((*bevelDocStrucHdl)->windowHeaderRef = GetNewControl(cWindowHeader,windowRef)))		ExitToShell();	// иииииииииииииииииииииииииииииии if running on OS X, make first four bevel buttons rounded	if(gRunningOnX)	{		SetControlData((*bevelDocStrucHdl)->bevelButton1Ref,kControlEntireControl,									 kControlBevelButtonKindTag,sizeof(buttonKind),&buttonKind);		SetControlData((*bevelDocStrucHdl)->bevelButton2Ref,kControlEntireControl,									 kControlBevelButtonKindTag,sizeof(buttonKind),&buttonKind);		SetControlData((*bevelDocStrucHdl)->bevelButton3Ref,kControlEntireControl,									 kControlBevelButtonKindTag,sizeof(buttonKind),&buttonKind);		SetControlData((*bevelDocStrucHdl)->bevelButton4Ref,kControlEntireControl,									 kControlBevelButtonKindTag,sizeof(buttonKind),&buttonKind);	}	// ииииииииииииииииииииииииииииииииииииииии set text placement for 2nd and 21st bevel button	SetControlData((*bevelDocStrucHdl)->bevelButton2Ref,kControlEntireControl,								 kControlBevelButtonTextPlaceTag,sizeof(textPlacementBelow),								 &textPlacementBelow);	SetControlData((*bevelDocStrucHdl)->bevelButton21Ref,kControlEntireControl,								 kControlBevelButtonTextPlaceTag,sizeof(textPlacementAbove),								 &textPlacementAbove);								 	// ииииииииииииииииииииииииииииииииииииииии set position of pop-up arrow in 6th bevel button	SetControlData((*bevelDocStrucHdl)->bevelButton6Ref,kControlEntireControl,								 kControlBevelButtonCenterPopupGlyphTag,sizeof(centrePopupGlyph),								 &centrePopupGlyph);	// ииииииииииииииииииииииииииииииии set font for 20th bevel button to small bold system font	controlFontStyleStruc.flags = kControlUseFontMask;	controlFontStyleStruc.font = kControlFontSmallBoldSystemFont;	SetControlFontStyle((*bevelDocStrucHdl)->bevelButton20Ref,&controlFontStyleStruc);	// иииииииииииииииииииииииииииииииииииииииииииииииии set 3rd bevel button to the mixed state	SetControlValue((*bevelDocStrucHdl)->bevelButton3Ref,2);}// *************************************************************** doBevelImagePictIconContentvoid  doBevelImagePictIconContent(EventRecord *eventStrucPtr,WindowRef windowRef){	BevelDocStrucHandle	bevelDocStrucHdl;	ControlRef					controlRef;	SInt16							partCode, menuItem;	bevelDocStrucHdl = (BevelDocStrucHandle) (GetWRefCon(windowRef));	SetPortWindowPort(windowRef);	GlobalToLocal(&eventStrucPtr->where);	partCode = FindControl(eventStrucPtr->where,windowRef,&controlRef);	doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,1234);	if(partCode)	{		partCode = TrackControl(controlRef,eventStrucPtr->where,NULL);		if(controlRef == (*bevelDocStrucHdl)->bevelButton1Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton2Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton3Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton4Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton9Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton13Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton14Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton15Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton16Ref ||			 controlRef == (*bevelDocStrucHdl)->bevelButton17Ref)			{			doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);		}		else if(controlRef == (*bevelDocStrucHdl)->bevelButton5Ref ||						controlRef == (*bevelDocStrucHdl)->bevelButton6Ref ||						controlRef == (*bevelDocStrucHdl)->bevelButton7Ref ||						controlRef == (*bevelDocStrucHdl)->bevelButton8Ref)		{			if(partCode == kControlMenuPart)			{				GetBevelButtonMenuValue(controlRef,&menuItem);				doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,menuItem);			}			else				doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);		}		else if(controlRef == (*bevelDocStrucHdl)->bevelButton10Ref ||						controlRef == (*bevelDocStrucHdl)->bevelButton11Ref ||						controlRef == (*bevelDocStrucHdl)->bevelButton12Ref)				{			if(partCode != kControlEntireControl)			{				SetControlValue((*bevelDocStrucHdl)->bevelButton10Ref,0);				SetControlValue((*bevelDocStrucHdl)->bevelButton11Ref,0);				SetControlValue((*bevelDocStrucHdl)->bevelButton12Ref,0);				SetControlValue(controlRef,1);			}			doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);		}		else if(controlRef == (*bevelDocStrucHdl)->bevelButton18Ref)		{			doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);			doGraphicAlignment(windowRef,controlRef,(*bevelDocStrucHdl)->windowHeaderRef);		}		else if(controlRef == (*bevelDocStrucHdl)->bevelButton19Ref)		{			doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);			doTextAlignment(windowRef,controlRef,(*bevelDocStrucHdl)->windowHeaderRef);		}		else if(controlRef == (*bevelDocStrucHdl)->bevelButton20Ref)		{			doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);			doTextOffset(windowRef,controlRef,(*bevelDocStrucHdl)->windowHeaderRef);		}		else if(controlRef == (*bevelDocStrucHdl)->bevelButton21Ref)		{			doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);			doTextPlacement(windowRef,controlRef,(*bevelDocStrucHdl)->windowHeaderRef);		}		else			doDrawPartCode(windowRef,(*bevelDocStrucHdl)->windowHeaderRef,partCode,4321);	}}// **************************************************************************** doDrawPartCodevoid  doDrawPartCode(WindowRef windowRef,ControlRef windowHeaderRef,SInt16 partCode,										 SInt16 menuItem){	SInt16	stringIndex;	Str255	theString;	if(partCode == kControlEntireControl)		stringIndex = 1;	else if(partCode == kControlMenuPart)		stringIndex = 2;	else if(partCode == kControlTrianglePart)		stringIndex = 3;	else if(partCode == kControlEditTextPart)		stringIndex = 4;	else if(partCode == kControlPicturePart)		stringIndex = 5;	else if(partCode == kControlIconPart)		stringIndex = 6;	else if(partCode == kControlClockPart)		stringIndex = 7;	else if(partCode == kControlListBoxPart)		stringIndex = 8;	else if(partCode == kControlListBoxDoubleClickPart)		stringIndex = 9;	else if(partCode == kControlImageWellPart)		stringIndex = 10;	else if(partCode == kControlRadioGroupPart)		stringIndex = 11;	else if(partCode == kControlButtonPart)		stringIndex = 12;	else if(partCode == kControlIndicatorPart)		stringIndex = 13;	if(menuItem > 0 && menuItem < 1234)	{		doCopyPString("\pTrackControl returned ",gCurrentString);		GetIndString(theString,rPartCodeStrings,stringIndex);		doConcatPStrings(gCurrentString,theString);		doConcatPStrings(gCurrentString,"\p    GetBevelButtonMenuValue returned menu item ");		NumToString((SInt32) menuItem,theString);		doConcatPStrings(gCurrentString,theString);	}	else if(menuItem == 1234 || menuItem == 4321)	{		if(menuItem == 1234)			doCopyPString("\pMouse-down in ",gCurrentString);		else if(menuItem == 4321)			doCopyPString("\pTrackControl returned ",gCurrentString);		GetIndString(theString,rPartCodeStrings,stringIndex);		doConcatPStrings(gCurrentString,theString);	}	Draw1Control(windowHeaderRef);	doDrawMessage(windowRef,true);}// ************************************************************************ doGraphicAlignmentvoid  doGraphicAlignment(WindowRef windowRef,ControlRef controlRef,ControlRef windowHeaderRef){	SInt16												a, b;	UInt32												finalTicks;	ControlButtonGraphicAlignment	alignmentConstant = 0;		if(!gRunningOnX)		SetThemeCursor(kThemeWatchCursor);	for(a=1;a<10;a++)	{		Delay(60,&finalTicks);		alignmentConstant++;		if(alignmentConstant == 9)			alignmentConstant = 0;		Draw1Control(windowHeaderRef);		GetIndString(gCurrentString,rGraphicAlignStrings,a);		doDrawMessage(windowRef,true);		for(b=0;b<=52;b++)		{			SetBevelButtonGraphicAlignment(controlRef,alignmentConstant,b,b);			Delay(2,&finalTicks);			Draw1Control(controlRef);			QDFlushPortBuffer(GetWindowPort(FrontNonFloatingWindow()),NULL);		}	}	if(!gRunningOnX)		SetThemeCursor(kThemeArrowCursor);}// *************************************************************************** doTextAlignmentvoid  doTextAlignment(WindowRef windowRef,ControlRef controlRef,ControlRef windowHeaderRef){	SInt16											a, b;	UInt32											finalTicks;	ControlButtonTextAlignment	alignmentConstant = -3;	if(!gRunningOnX)		SetThemeCursor(kThemeWatchCursor);	for(a=1;a<4;a++)	{		Delay(60,&finalTicks);		alignmentConstant++;		if(alignmentConstant == 0)			alignmentConstant++;		Draw1Control(windowHeaderRef);		GetIndString(gCurrentString,rTextAlignStrings,a);		doDrawMessage(windowRef,true);		for(b=0;b<=40;b++)		{			SetBevelButtonTextAlignment(controlRef,alignmentConstant,b);			Delay(2,&finalTicks);			Draw1Control(controlRef);			QDFlushPortBuffer(GetWindowPort(FrontNonFloatingWindow()),NULL);		}	}	if(!gRunningOnX)		SetThemeCursor(kThemeArrowCursor);}// ****************************************************************************** doTextOffsetvoid  doTextOffset(WindowRef windowRef,ControlRef controlRef,ControlRef windowHeaderRef){	ControlButtonTextAlignment	alignmentConstant;	SInt16											offset;	UInt32											finalTicks;	if(!gRunningOnX)		SetThemeCursor(kThemeWatchCursor);	Draw1Control(windowHeaderRef);	doCopyPString("\pOffset from left",gCurrentString);	doDrawMessage(windowRef,true);	alignmentConstant = kControlBevelButtonAlignTextFlushLeft;	SetControlData(controlRef,kControlEntireControl,kControlBevelButtonTextAlignTag,									sizeof(alignmentConstant),&alignmentConstant);	Draw1Control(controlRef);	for(offset=1;offset<27;offset++)	{		Delay(15,&finalTicks);		SetControlData(controlRef,kControlEntireControl,kControlBevelButtonTextOffsetTag,									 sizeof(offset),&offset);		Draw1Control(controlRef);		QDFlushPortBuffer(GetWindowPort(FrontNonFloatingWindow()),NULL);	}	Delay(60,&finalTicks);	Draw1Control(windowHeaderRef);	doCopyPString("\pOffset from right",gCurrentString);	doDrawMessage(windowRef,true);	alignmentConstant = kControlBevelButtonAlignTextFlushRight;	SetControlData(controlRef,kControlEntireControl,kControlBevelButtonTextAlignTag,									sizeof(alignmentConstant),&alignmentConstant);	for(offset=0;offset<13;offset++)	{		Delay(15,&finalTicks);		SetControlData(controlRef,kControlEntireControl,kControlBevelButtonTextOffsetTag,									 sizeof(offset),&offset);		Draw1Control(controlRef);		QDFlushPortBuffer(GetWindowPort(FrontNonFloatingWindow()),NULL);	}	if(!gRunningOnX)		SetThemeCursor(kThemeArrowCursor);}// *************************************************************************** doTextPlacementvoid  doTextPlacement(WindowRef windowRef,ControlRef controlRef,ControlRef windowHeaderRef){	ControlButtonTextPlacement	placementConstant;	UInt32											finalTicks;	if(!gRunningOnX)		SetThemeCursor(kThemeWatchCursor);	for(placementConstant = 1;placementConstant < 5;placementConstant++)	{		Delay(60,&finalTicks);		SetBevelButtonTextPlacement(controlRef,placementConstant);		Draw1Control(controlRef);		Draw1Control(windowHeaderRef);		GetIndString(gCurrentString,rTextPlacementStrings,placementConstant);		doDrawMessage(windowRef,true);		QDFlushPortBuffer(GetWindowPort(FrontNonFloatingWindow()),NULL);	}	if(!gRunningOnX)		SetThemeCursor(kThemeArrowCursor);}// ***************************************************************************** doDrawMessagevoid  doDrawMessage(WindowRef windowRef,Boolean inState){	Rect				portRect;	CFStringRef	stringRef;	Rect				textBoxRect;	if(windowRef == gWindowRef)	{		SetPortWindowPort(windowRef);		GetWindowPortBounds(windowRef,&portRect);		stringRef = CFStringCreateWithPascalString(NULL,gCurrentString,kCFStringEncodingMacRoman);		SetRect(&textBoxRect,portRect.left,7,portRect.right,22);		if(inState == kThemeStateActive)			TextMode(srcOr);		else			TextMode(grayishTextOr);		DrawThemeTextBox(stringRef,kThemeSmallSystemFont,inState,true,&textBoxRect,teJustCenter,											 NULL);		if(stringRef != NULL)			CFRelease(stringRef);	}}// ***************************************************************************** doDrawLegendsvoid  doDrawLegends(WindowRef windowRef,Boolean inState){	Rect	theRect;	if(windowRef == gWindowRef)	{		SetPortWindowPort(windowRef);		if(gRunningOnX)		{			SetRect(&theRect,30,54,585,67);			EraseRect(&theRect);			OffsetRect(&theRect,0,91);			EraseRect(&theRect);			OffsetRect(&theRect,0,92);			EraseRect(&theRect);		}		if(inState == kThemeStateActive)			TextMode(srcOr);		else	  	TextMode(grayishTextOr);		SetRect(&theRect,30,53,220,68);		if(gRunningOnX)			DrawThemeTextBox(CFSTR("Bevel button content"),kThemeSmallSystemFont,inState,false,											 &theRect,teJustLeft,NULL);		else			DrawThemeTextBox(CFSTR("Bevel sizes and bevel button content"),kThemeSmallSystemFont,											 inState,false,&theRect,teJustLeft,NULL);		SetRect(&theRect,313,53,513,68);		DrawThemeTextBox(CFSTR("Menu position and behaviour"),kThemeSmallSystemFont,inState,										 false,&theRect,teJustLeft,NULL);		SetRect(&theRect,30,144,180,159);		DrawThemeTextBox(CFSTR("Bevel button behaviour"),kThemeSmallSystemFont,inState,false,										 &theRect,teJustLeft,NULL);		SetRect(&theRect,313,144,513,159);		DrawThemeTextBox(CFSTR("Graphic/text alignment & offset"),kThemeSmallSystemFont,inState,										 false,&theRect,teJustLeft,NULL);		SetRect(&theRect,490,144,640,159);		DrawThemeTextBox(CFSTR("Text placement"),kThemeSmallSystemFont,inState,false,&theRect,										 teJustLeft,NULL);		SetRect(&theRect,30,235,180,250);		DrawThemeTextBox(CFSTR("Image wells"),kThemeSmallSystemFont,inState,false,&theRect,										 teJustLeft,NULL);		SetRect(&theRect,168,235,308,250);		DrawThemeTextBox(CFSTR("Picture controls"),kThemeSmallSystemFont,inState,false,&theRect,										 teJustLeft,NULL);		SetRect(&theRect,313,235,463,250);		DrawThemeTextBox(CFSTR("Icon controls (cicn)"),kThemeSmallSystemFont,inState,false,										 &theRect,teJustLeft,NULL);		SetRect(&theRect,451,235,601,250);		DrawThemeTextBox(CFSTR("Icon controls (icon suite)"),kThemeSmallSystemFont,inState,false,										 &theRect,teJustLeft,NULL);	}}// **************************************************************** helpTagsBevelImagePictIconvoid  helpTagsBevelImagePictIcon(