// *******************************************************************************************// TextBox.c//********************************************************************************************// ииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии includes#include "Controls3.h"// ********************************************************************************* doTextBoxvoid  doTextBox(void){	DialogRef				dialogRef;	ModalFilterUPP	eventFilterUPP;	SInt16					itemHit;	if(FrontNonFloatingWindow())		doActivateWindow(FrontNonFloatingWindow(),false);	if(!(dialogRef = GetNewDialog(rAboutDialog,NULL,(WindowRef) -1)))		ExitToShell();	// ииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииииии set default button	SetDialogDefaultItem(dialogRef,kStdOkItemIndex);		// иииииииииииииииииииииииииииииииииииии create universal procedure pointer for event filter	eventFilterUPP = NewModalFilterUPP((ModalFilterProcPtr) eventFilter);	// иииииииииииииииииииииииииииииииииииииииииииииииии show dialog and enter modal dialog loop	ShowWindow(GetDialogWindow(dialogRef));	do	{		ModalDialog(eventFilterUPP,&itemHit);	} while(itemHit != kStdOkItemIndex);	DisposeDialog(dialogRef);	DisposeModalFilterUPP(eventFilterUPP);}// *******************************************************************************************